document.getElementById('scrollButton').addEventListener('click', function() {
    const nextSection = document.querySelector('main > section:nth-of-type(2)'); // Вибирає наступний розділ (наприкінці вашого HTML).
    if (nextSection) {
        // Обчислює цільову позицію
        const targetPosition = nextSection.getBoundingClientRect().top + window.scrollY;

        // Плавна прокрутка до цільової позиції
        window.scrollTo({
            top: targetPosition,
            behavior: 'smooth' // Це активує плавну прокрутку
        });
    }
});